import React, { useState } from 'react'
import './Search.css'
import { Search, Mic } from '@material-ui/icons';
import { Button } from '@material-ui/core';
import { useHistory } from 'react-router';
import { actionTypes } from '../reducer'
import { StateValues } from '../StateProvider'
import useGoogleSearch from './useGoogleSearch'


function SearchPage({ hideButton = false }) {

    const [input, setInput] = useState('')
    const { data } = useGoogleSearch(term ? term : '')

    const history = useHistory()

    const [term, dispatch] = StateValues()
    console.log(term, 'searchpage')

    const search = (e) => {
        console.log(e.target.value,data, 'onchange');
        e.preventDefault()
        history.push('/search')
        dispatch({
            type: actionTypes.SET_SEATCH_TERM,
            term: input
        })
    }

    return (
        <form className='search'>
            <div className='search__input'>
                <Search className='search__inputIcon' />
                <input value={input}
                    onChange={(e) => setInput(e.target.value)} />
                <Mic />

            </div>
            {
                !hideButton ? (
                    <div className='search__buttons'>
                        <Button variant='outlined' type='submit' onClick={search} >Google Search</Button>
                        <Button variant='outlined'>I'm Feeling Lucky</Button>

                    </div>
                ) : (
                        <div className='search__buttons'>
                            <Button className='buttons_hide' variant='outlined' type='submit' onClick={search} >Google Search</Button>
                            <Button className='buttons_hide' variant='outlined'>I'm Feeling Lucky</Button>

                        </div>
                    )
            }


        </form>
    )
}

export default SearchPage
