export const initialState = {
    term: null,
    searchedData:[]
}

export const actionTypes = {
    SET_SEATCH_TERM: 'SET_SEARCH_TERM'
}

const reducer = (state, action) => {
    console.log(action)
    switch (action.type) {
        case 'SET_SEARCH_TERM':
            return {
                ...state,
                term: action.term,
                // searchedData:[...state.searchedData,action.resultArray]
            }


        default:
            return state;

    }
}

export default reducer;
